using System.Collections.Generic;
using System;

class Helpers {
    public struct ChainInfo {
        public string chain;
        public string network;
        public string starshipsContract;
        public string readerContract;
        public string rpc;
        public string display;
    }

    public static List<string> Archetypes = new List<string>{"Dragonfly", "Hercules", "Horizon", "Javelin", "Omen","Pearl", "Shuttle", "Starhammer", "Vanguard", "Viper"};

    public static int BoolToInt32(bool value) {
        if(value) return 1;

        return 0;
    }

    public static bool Int32ToBool(int value) {
        if(value == 0) return false;

        return true;
    }

    public static ChainInfo GetChainInfo(int chainId) {
        ChainInfo info = new ChainInfo();
        if(chainId == 137) {
            info.chain = "polygon";
            info.network = "mainnet";
            info.display = "Polygon";
            #if UNITY_EDITOR
            info.starshipsContract = "0x130c72b19c322636EBB08d3B72090651DA15106f";
            info.readerContract = "0xD31130b49b1E95e843407E5fFc568C65f6F167D0";
            #else
            info.starshipsContract = "0x7da4a57dC3f4660a2CF843e8bAdD21835b394c22";
            info.readerContract = "0xE013a765f6E6541b77C7e3c02FF31C6880148166";
            #endif
            info.rpc = ""; // use their rpc
        }
        else if(chainId == 250) {
            info.chain = "fantom";
            info.network = "mainnet";
            info.display = "Fantom";
            #if UNITY_EDITOR
            info.starshipsContract = "0xfe440939C8b2E8d59b21229e13fCf7f8cf260e6b";
            info.readerContract = "0x939E3e6B2055d6be4F4a6B7189aD0ffa9BfFc77d";
            #else
            info.starshipsContract = "0xfe440939C8b2E8d59b21229e13fCf7f8cf260e6b";
            info.readerContract = "0x939E3e6B2055d6be4F4a6B7189aD0ffa9BfFc77d";
            #endif
            info.rpc = "https://rpc.ftm.tools/";
        }

        return info;
    }
}

